import React, { useState } from 'react';
const Synchfour =()=>{
    
     const [c1,setC1] = useState();
     const [x1,setX1]= useState();
     const [c2,setC2] = useState();
     const [x2,setX2]= useState();
     const [c3,setC3] = useState();
     const [x3,setX3]= useState(); 
    const chng1=(event)=>{
        setC1(event.target.value);
    }
    const chng2=(event)=>{
        setC2(event.target.value);
    }
    const chng3=(event)=>{
        setC3(event.target.value);
    }
    const evaluate=()=>{
        setX1(c1);
        setX2(c2);
        setX3(c3);
        // eslint-disable-next-line
       var arr1 = c1.split(',').map(function(c1){return Number(c1);});
      // eslint-disable-next-line
       var arr2 = c2.split(',').map(function(c2){return Number(c2);});
       var arr3 = c3.split(',').map(function(c3){return Number(c3);});
    };
    return(
        <>
            <h1>Synchronous 4-bit</h1>
           
              <input type="text" className="inp" id="dff1" placeholder="1,2,3.." onChange={chng1} value={c1}/> 
              <input type="text" className="inp" id="dff2" placeholder="1,2,3.." onChange={chng2} value={c2}/> 
              <input type="text" className="inp" id="dff3" placeholder="1,2,3.." onChange={chng3} value={c3}/>  
              <button type="submit" className="clickme" onClick={evaluate} >Go ğŸ™‚</button>

        </>
    );
};
export default Synchfour;
