import React, { useState } from 'react';
import './index.css';
const Synchtwo = ()=>{
    
    const [c,setC] = useState({
        c1:"",
        c2:""
    });
    const [x,setX]= useState({
        x1:"",
        x2:""
    });
    const [v1,setV1]= useState({
        v10:"",
        v11:"",
        v12:"",
        v13:""
    });
    const [v2,setV2]= useState({
        v20:"",
        v21:"",
        v22:"",
        v23:""
    });
    var vv1 = [0,0,0,0];
    var vv2 = [0,0,0,0];
   const chng=(event)=>{
      const value =event.target.value;
      const name =event.target.name;
      setC((preVal)=>{
        if(name==="cc1"){
            return{c1:value,
            c2:preVal.c2
            };
        };
        if(name==="cc2"){
            return{c2:value,
            c1:preVal.c1
            };
        };
      });
   }
   const evaluate=()=>{
       setX({
           x1:c.c1,
           x2:c.c2
       });
       var ss= c.c1;
      var arr1 = ss.split(',').map(function(ss){return Number(ss);});
      var arr2 = c2.split(',').map(function(c2){return Number(c2);});
        for(var i=0;i<=arr1.length;i++){
            v1[arr1[i]]=1;  
        }
   };
   return(
       <>
           <h1>Synchronous 2-bit</h1>
          
             <input type="text" className="inp" id="dff1" placeholder="1,2,3.." name="cc1" onChange={chng} value={c.c1}/> 
             <input type="text" className="inp" id="dff2" placeholder="1,2,3.." name="cc2" onChange={chng} value={c.c2}/> 
             <button type="submit" className="clickme" onClick={evaluate} >Go 🙂</button>
             <table className="tg">
<thead>
  <tr>
    <th className="tg-0pky" colSpan="2">Present State</th>
    <th className="tg-0pky" colSpan="2">Flip-Flop Inputs</th>
    <th className="tg-0pky" colSpan="2">Next State</th>
  </tr>
  <tr>
    <td className="tg-0pky">A</td>
    <td className="tg-0pky">B</td>
    <td className="tg-0pky">T<sub>A</sub></td>
    <td className="tg-0pky">T<sub>B</sub></td>
    <td className="tg-0pky">A(n+1)</td>
    <td className="tg-0pky">B(n+1)</td>
  </tr>
</thead>
<tbody>
  <tr>
    <td className="tg-0pky">0</td>
    <td className="tg-0pky">0</td>
    <td className="tg-0pky">{v10}</td>
    <td className="tg-0pky"></td>
    <td className="tg-0pky"></td>
    <td className="tg-0pky"></td>
  </tr>
  <tr>
    <td className="tg-0pky">0</td>
    <td className="tg-0pky">1</td>
    <td className="tg-0pky">{v1[1]}</td>
    <td className="tg-0pky"></td>
    <td className="tg-0pky"></td>
    <td className="tg-0pky"></td>
  </tr>
  <tr>
    <td className="tg-0pky">1</td>
    <td className="tg-0pky">0</td>
    <td className="tg-0pky">{v1[2]}</td>
    <td className="tg-0pky"></td>
    <td className="tg-0pky"></td>
    <td className="tg-0pky"></td>
  </tr>
  <tr>
    <td className="tg-0pky">1</td>
    <td className="tg-0pky">1</td>
    <td className="tg-0pky">{v1[3]}</td>
    <td className="tg-0pky"></td>
    <td className="tg-0pky"></td>
    <td className="tg-0pky"></td>
  </tr>
</tbody>
</table>
       </>
   );
};
export default Synchtwo;
