import React, { useState } from 'react';
import './index.css';
import cytoscape from 'cytoscape';
import dagre from 'cytoscape-dagre';
cytoscape.use( dagre );
const Synchtwo = ()=>{
    
    const [c,setC] = useState({
        c1:"",
        c2:""
    });
    const [v1,setV1]= useState({
        v10:"",
        v11:"",
        v12:"",
        v13:""
    });
    const [v2,setV2]= useState({
        v20:"",
        v21:"",
        v22:"",
        v23:""
    });
    const [n1,setN1]= useState({
        n10:"",
        n11:"",
        n12:"",
        n13:""
    });
    const [n2,setN2]= useState({
        n20:"",
        n21:"",
        n22:"",
        n23:""
    });
   const chng=(event)=>{
      const value =event.target.value;
      const name =event.target.name;
      setC((preVal)=>{
        if(name==="cc1"){
            return{c1:value,
            c2:preVal.c2
            };
        };
        if(name==="cc2"){
            return{c2:value,
            c1:preVal.c1
            };
        };
      });
   }
   const evaluate=()=>{
    var vv1 = [0,0,0,0];
    var vv2 = [0,0,0,0];
    var nn1 = [0,0,0,0];
    var nn2 = [0,0,0,0];
       var ss= c.c1;
       var ss2= c.c2;
      var arr1 = ss.split(',').map(function(ss){return Number(ss);});
      var arr2 = ss2.split(',').map(function(ss2){return Number(ss2);});
        for(var i=0;i<=arr1.length;i++){
            vv1[arr1[i]]=1;  
        }
        for(i=0;i<=arr2.length;i++){
            vv2[arr2[i]]=1;  
        }
        setV1({
        v10:vv1[0],
        v11:vv1[1],
        v12:vv1[2],
        v13:vv1[3],
        });
         setV2({
        v20:vv2[0],
        v21:vv2[1],
        v22:vv2[2],
        v23:vv2[3],
        });

        for(i=0;i<=4;i++){
            var x=(i-i%2)/2;
            if(vv1[i]===1){
                if(x===1){
                    nn1[i]=0;
                }
                else{
                    nn1[i]=1;
                }
            }
            else{
                nn1[i]=(x);
            }
            if(vv2[i]===1){
                if(i%2===1){
                    nn2[i]=0;
                }
                else{
                    nn2[i]=1;
                }
            }
            else{
                nn2[i]=(i%2);
            }
        }
        console.log(vv1);
        console.log(vv2);
        console.log(nn1);
        console.log(nn2);
        setN1({
            n10:nn1[0],
            n11:nn1[1],
            n12:nn1[2],
            n13:nn1[3]
            });
        setN2({
            n20:nn2[0],
            n21:nn2[1],
            n22:nn2[2],
            n23:nn2[3]
            });
            cytoscape({
                container: document.getElementById('root'),
      
                boxSelectionEnabled: false,
                autounselectify: true,
      
                layout: {
                  name: 'dagre'
                },
      
                style: [
                  {
                    selector: 'node',
                    style: {
                      'background-color': '#11479e'
                    }
                  },
      
                  {
                    selector: 'edge',
                    style: {
                      'width': 4,
                      'target-arrow-shape': 'triangle',
                      'line-color': '#9dbaea',
                      'target-arrow-color': '#9dbaea',
                      'curve-style': 'bezier'
                    }
                  }
                ],
      
                elements: {
                  nodes: [
                    { data: { id: 'n0' } },
                    { data: { id: 'n1' } },
                    { data: { id: 'n2' } },
                    { data: { id: 'n3' } },
                    { data: { id: 'n4' } },
                    { data: { id: 'n5' } },
                    { data: { id: 'n6' } },
                    { data: { id: 'n7' } },
                    { data: { id: 'n8' } },
                    { data: { id: 'n9' } },
                    { data: { id: 'n10' } },
                    { data: { id: 'n11' } },
                    { data: { id: 'n12' } },
                    { data: { id: 'n13' } },
                    { data: { id: 'n14' } },
                    { data: { id: 'n15' } },
                    { data: { id: 'n16' } }
                  ],
                  edges: [
                    { data: { source: 'n0', target: 'n1' } },
                    { data: { source: 'n1', target: 'n2' } },
                    { data: { source: 'n1', target: 'n3' } },
                    { data: { source: 'n4', target: 'n5' } },
                    { data: { source: 'n4', target: 'n6' } },
                    { data: { source: 'n6', target: 'n7' } },
                    { data: { source: 'n6', target: 'n8' } },
                    { data: { source: 'n8', target: 'n9' } },
                    { data: { source: 'n8', target: 'n10' } },
                    { data: { source: 'n11', target: 'n12' } },
                    { data: { source: 'n12', target: 'n13' } },
                    { data: { source: 'n13', target: 'n14' } },
                    { data: { source: 'n13', target: 'n15' } },
                  ]
                }
              });
      
   };
   return(
       <>
           <h1>Synchronous 2-bit</h1>
          
             <input type="text" className="inp" id="dff1" placeholder="1,2,3.." name="cc1" onChange={chng} value={c.c1}/> 
             <input type="text" className="inp" id="dff2" placeholder="1,2,3.." name="cc2" onChange={chng} value={c.c2}/> 
